    private void jBTSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTSalvarActionPerformed
        if (!jTFNome.getText().equals("")) {
            Pessoa pessoa = new Pessoa();
            if (pessoaId != -1) {
                pessoa.setPessoaId(pessoaId);
            }
            pessoa.setNome(jTFNome.getText());
            pessoa.setIdade(jTFIdade.getText());
            pessoa.setDataNascimento(jTFDataNascimento.getText());
            pessoa.setCidade(jTFCidade.getText());

            if (jRBCasado.isSelected()) {
                pessoa.setEstadoCivil("Casado");
            } else if (jRBSolteiro.isSelected()) {
                pessoa.setEstadoCivil("Solteiro");
            } else if (jRBViuvo.isSelected()) {
                pessoa.setEstadoCivil("Viuvo");
            } else {
                buttonGroup1.clearSelection();
            }
            PessoaDAO pessoadao = new PessoaDAOMySQL();
            if (pessoadao.salvar(pessoa)) {
                javax.swing.JOptionPane.showMessageDialog(null, "Pessoa cadastrado com suscesso!");
            } else {
                javax.swing.JOptionPane.showMessageDialog(null, "Pessoa n√£o cadastrado!");
            }
        } else {
            javax.swing.JOptionPane.showMessageDialog(null, "O Nome deve ser preenchido!");
            jTFNome.requestFocus();
        }
    }//GEN-LAST:event_jBTSalvarActionPerformed

