    private void jBTExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTExcluirActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser exclu√≠do");
        PessoaDAO pessoadao = new PessoaDAOMySQL();
        Pessoa pessoa = pessoadao.consultarPK(id);
        if (pessoa != null) {
            jTFNome.setText(pessoa.getNome());
            jTFIdade.setText(pessoa.getIdade());
            jTFDataNascimento.setText(pessoa.getDataNascimento());
            jTFCidade.setText(pessoa.getCidade());
            if (pessoa.getEstadoCivil().equalsIgnoreCase("Casado")) {
                jRBCasado.setSelected(true);
                jRBSolteiro.setSelected(false);
                jRBViuvo.setSelected(false);
            } else if (pessoa.getEstadoCivil().equalsIgnoreCase("Solteiro")) {
                jRBCasado.setSelected(false);
                jRBSolteiro.setSelected(true);
                jRBViuvo.setSelected(false);
            } else if (pessoa.getEstadoCivil().equalsIgnoreCase("Viuvo")) {
                jRBCasado.setSelected(false);
                jRBSolteiro.setSelected(false);
                jRBViuvo.setSelected(true);
            } else {
                buttonGroup1.clearSelection();
            }

            Object[] opcoes = {"Sim", "N√£o"};
            int resposta = javax.swing.JOptionPane.showOptionDialog(null, "Confirma a exclus√£o do Pessoa?", "Exclus√£o", 0, javax.swing.JOptionPane.QUESTION_MESSAGE, null, opcoes, opcoes[1]);
            if (resposta == 0) {
                if (pessoadao.apagarPK(Integer.parseInt(id))) {
                    javax.swing.JOptionPane.showMessageDialog(null, "Pessoa exclu√≠do com suscesso!");
                    jTFNome.setText("");
                    jTFIdade.setText("");
                    jTFDataNascimento.setText("");
                    jTFCidade.setText("");
                } else {
                    javax.swing.JOptionPane.showMessageDialog(null, "Pessoa n√£o exclu√≠do!");
                }
            }
        }
    }//GEN-LAST:event_jBTExcluirActionPerformed

    private void JBTAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBTAlterarActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser alterado");
        PessoaDAO pessoadao = new PessoaDAOMySQL();
        Pessoa pessoa = pessoadao.consultarPK(id);
        if (pessoa != null) {
            pessoaId = pessoa.getPessoaId();
            jTFNome.setText(pessoa.getNome());
            jTFIdade.setText(pessoa.getIdade());
            jTFDataNascimento.setText(pessoa.getDataNascimento());
            jTFCidade.setText(pessoa.getCidade());
            if (pessoa.getEstadoCivil().equalsIgnoreCase("Casado")) {
                jRBCasado.setSelected(true);
                jRBSolteiro.setSelected(false);
                jRBViuvo.setSelected(false);
            } else if (pessoa.getEstadoCivil().equalsIgnoreCase("Solteiro")) {
                jRBCasado.setSelected(false);
                jRBSolteiro.setSelected(true);
                jRBViuvo.setSelected(false);
            } else if (pessoa.getEstadoCivil().equalsIgnoreCase("Viuvo")) {
                jRBCasado.setSelected(false);
                jRBSolteiro.setSelected(false);
                jRBViuvo.setSelected(true);
            } else {
                jRBCasado.setSelected(false);
                jRBSolteiro.setSelected(false);
                jRBViuvo.setSelected(false);
            }
        }
    }//GEN-LAST:event_JBTAlterarActionPerformed

